namespace Ecommerce\Creditlimit\Model\Total\Quote;

/**
 * Calculate applied store credit in cart
 *
 * @SuppressWarnings(PHPMD.CookieAndSessionMisuse)
 */
class Discount extends DiscountAbstract
{
    protected $_code = 'creditdiscount';

    /**
     * Collect address discount amount
     *
     * @param \Magento\Quote\Model\Quote $quote
     * @param \Magento\Quote\Api\Data\ShippingAssignmentInterface $shippingAssignment
     * @param \Magento\Quote\Model\Quote\Address\Total $total
     */
    public function collect(
        \Magento\Quote\Model\Quote $quote,
        \Magento\Quote\Api\Data\ShippingAssignmentInterface $shippingAssignment,
        \Magento\Quote\Model\Quote\Address\Total $total
    ) {
        parent::collect($quote, $shippingAssignment, $total);
        
        $this->calculateDiscount($quote, $shippingAssignment, $total, true);
    }
}
